execution.script:
node('slave-1'){

    def mvnHome = tool name: 'MAVEN3', type: 'maven'
    def mvnCli = "${mvnHome}/bin/mvn"


    properties([buildDiscarder(logRotator(artifactDaysToKeepStr: '', artifactNumToKeepStr: '', daysToKeepStr: '', numToKeepStr: '30')), disableConcurrentBuilds(), [$class: 'GithubProjectProperty', displayName: '', projectUrlStr: 'http://github.com/gkdevops/PetClinic.git/'], parameters([string(defaultValue: 'DEV', description: '', name: 'environment', trim: false)]), pipelineTriggers([githubPush()])])

    stage('Checkout Code'){
        git credentialsId: 'b682e002-bdfa-4f9a-9dd2-5ac74d2ddbd2', url: 'https://github.com/gkdevops/PetClinic.git'
    }

    stage('Read parameters'){
        echo "${params.environment}"
    }

    stage('Maven Test'){
        sh "${mvnCli} clean compile"
    }

    stage('Maven Package'){
        sh "${mvnCli} package -Dmaven.test.skip=true"
    }    

    stage('Deploy Code'){
        sshagent(['centos']) {
            sh 'scp -o StrictHostKeyChecking=no target/*.war centos@ec2-54-85-55-204.compute-1.amazonaws.com:/opt/apache-tomcat-7.0.94/webapps/'
        }
    }

    stage('smoke test'){
        sleep 10
        sh 'curl http://ec2-54-85-55-204.compute-1.amazonaws.com:8080'
    }


}
